                snapLines = _.sortBy(snapLines, function(snapLine1, snapLine2) {
                    return snapLine1.distance > snapLine2.distance ? 1 : (snapLine1.distance === snapLine2.distance ? 0 : -1);
                });

                snapLinesByAxis[axis] = snapLines;
