        var buttonWidth = option.buttonWidth || this.options.buttonWidth;
        if (buttonWidth) {
            $option.css('width', buttonWidth);
        }
        var buttonHeight = option.buttonHeight || this.options.buttonHeight;
        if (buttonHeight) {
            $option.css('height', buttonHeight);
        }
