        // would have no effect while overflow in FF and IE.
        // see 'https://bugzilla.mozilla.org/show_bug.cgi?id=748518'
        var margin = {
            marginBottom: Math.round(base + (bottom || 0)),
            marginRight: Math.round(base + (right || 0))
        };

